document.addEventListener("DOMContentLoaded", () => {
    new LazyLoad();

    if(document.getElementById('homeSlider')) {
        new Swiper('#homeSlider', {
            lazyPreloadPrevNext: 1,
            loop: true,
            spaceBetween: 10,
            navigation: {
                nextEl: '.swiper-next',
                prevEl: '.swiper-prev',
            },
            pagination: {
                el: '.home-slider__pagination',
                clickable: true,
            },
        });
    }

});

